const char* indexHtml = 
"<html>\n<head>\n    <script>\n        window.onload = function () {\n            var brewData = [];\n            var steamData = [];\n            var data = {\n                datasets: [{\n                    label: 'Temperature',\n                    data: [],\n                }, {\n                    label: 'Output',\n                    data: brewData,\n                }],\n            };\n\n            const options = {\n                interaction: {\n                    mode: 'nearest',\n                    axis: 'x',\n                    intersect: false\n                },\n                plugins: {\n                    decimation: {\n                        enabled: 'true',\n                        algorithm: 'lttb',\n                        samples: 50\n                    }\n                },\n                scales: {\n                    x: {\n                        type: 'timeseries',\n                        time: {\n                            unit: 'second',\n                            stepSize: 25,\n                        },\n                        ticks: {\n                            source: 'data',\n                            autoSkip: true,\n                            maxRotation: 10,\n                        },\n                    },\n                    y: {\n                        min: 0\n                    },\n                },\n            }\n\n            const actions = [{\n                name: \"Display Brew Temps\",\n                handler() {\n                    chart.update();\n                },\n            }, {\n                name: \"Display Steam Temps\",\n                handler() {\n                    chart.update();\n                }\n            }];\n\n            const ctx = document.getElementById('tempChart').getContext('2d');\n            var chart = new Chart(ctx, {\n                type: 'line',\n                data: data,\n                options: options,\n            });\n\n            const container = document.getElementById('tempContainer');\n            actions.forEach(function(action){\n                const button = document.createElement('button');\n                button.innerHTML = action.name;\n                button.onclick = action.handler;\n                container.appendChild(button);\n            });\n\n            var xhttp = new XMLHttpRequest();\n            xhttp.onreadystatechange = function(){\n                if (this.readyState == 4 && this.status == 200) {\n                    var tempData = JSON.parse(this.response);\n                    chart.data.datasets[0].data.push({x: Date.now(), y: tempData.temperature});\n                    brewData.push(tempData.brew);\n                    steamData.push(tempData.steam);\n                    chart.update();\n                    pollServer(this);\n                }\n            }\n            function pollServer(xhttp) {\n                console.log(xhttp);\n                xhttp.open(\"GET\", \"/get_temps\", true);\n                xhttp.send();\n                console.log('hello');\n            }\n            pollServer(xhttp);\n        }\n    </script>\n</head>\n<body>\n    <div id=\"tempContainer\" class=\"chart-container\" style=\"position:relative; height:40vh; width:120vh; margin:auto;\">\n        <canvas id=\"tempChart\"></canvas>\n    </div>\n    <script src=\"https://cdn.jsdelivr.net/npm/chart.js@3.3.2\"></script>\n    <script src=\"https://cdn.jsdelivr.net/npm/moment@2.29.1\"></script>\n    <script src=\"https://cdn.jsdelivr.net/npm/chartjs-adapter-moment@0.1.1\"></script>\n</body>\n</html>";